import{_ as e,o as l,c as i,a as c}from"./app-CUvceQeY.js";const d={},o=c('<h1 id="vim" tabindex="-1"><a class="header-anchor" href="#vim"><span>Vim</span></a></h1><h2 id="模式" tabindex="-1"><a class="header-anchor" href="#模式"><span>模式</span></a></h2><ul><li>普通模式：初次进入、在插入模式中按 <code>Esc</code></li><li>插入模式：在普通模式中输入 <code>i</code></li><li>命令模式：在普通模式中输入 <code>:</code></li></ul><h2 id="普通模式" tabindex="-1"><a class="header-anchor" href="#普通模式"><span>普通模式</span></a></h2><h3 id="移动光标" tabindex="-1"><a class="header-anchor" href="#移动光标"><span>移动光标</span></a></h3><ul><li><code>hjkl</code> 对应 <code>←↓↑→</code></li><li><code>w</code> 移动至下一个单词开头（可跨行）</li><li><code>b</code> 移动至前一个单词开头（可跨行）</li><li><code>f+单个字符</code> 移动至下一个对应字符之前 (仅针对当前行，当前行不存在则不移动)</li><li><code>F+单个字符</code> 移动至上一个对应字符之前（仅针对当前行，当前行不存在则不移动）</li><li><code>gg</code> 移动至文档开头</li><li><code>G</code> 移动至文档末尾</li><li><code>Ctrl+U</code> 等价于 PageUp，移动至上一页</li><li><code>Ctrl+D</code> 等价于 PageDown，移动至下一页</li></ul><div class="callout" data-callout="tip"><div class="callout-title"><div class="callout-title-icon"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-flame"><path d="M8.5 14.5A2.5 2.5 0 0 0 11 12c0-1.38-.5-2-1-3-1.072-2.143-.224-4.054 2-6 .5 2.5 2 4.9 4 6.5 2 1.6 3 3.5 3 5.5a7 7 0 1 1-14 0c0-1.153.433-2.294 1-3a2.5 2.5 0 0 0 2.5 2.5z"></path></svg></div><div class="callout-title-inner">数字+指令</div></div><div class="callout-content"><p><code>hjkl</code>、<code>w</code>、<code>b</code>、<code>f</code> 和 <code>F</code> 前面加上数字，表示连续执行若干次。如 <code>2w</code> 表示连续执行两次 <code>w</code> 指令，即光标移动到两个单词之后的单词开头。<code>2j</code> 表示连续执行两次 <code>j</code> 指令，即光标跳转到两行之后。</p></div></div><h3 id="删除、剪切、复制、粘贴" tabindex="-1"><a class="header-anchor" href="#删除、剪切、复制、粘贴"><span>删除、剪切、复制、粘贴</span></a></h3><ul><li><code>x</code> 进行删除</li><li><code>d</code> 进行剪切</li><li><code>y</code> 进行复制 <ul><li><code>y</code> 可以和光标移动指令组合使用，如： <ul><li><code>yaw</code>：复制整个当前单词</li><li><code>yb</code>：复制上一个单词</li><li><code>yj</code>：复制本行和下一行（一共复制两行）</li><li><code>y&lt;数字&gt;j</code>：复制本行和下若干行（一共复制数字+1 行）</li></ul></li></ul></li><li><code>p</code> 进行粘贴</li></ul><h2 id="插入模式" tabindex="-1"><a class="header-anchor" href="#插入模式"><span>插入模式</span></a></h2><ul><li><code>i</code>：在当前光标之前插入</li><li><code>a</code>：在当前光标之后插入</li><li><code>I</code>：在当前行最前面插入</li><li><code>A</code>：在当前行的末尾插入</li><li><code>c</code>：改变操作（删除并进入编辑模式） <ul><li><code>cc</code>：删除当前行并进入编辑模式</li><li><code>c</code> 可以和光标移动指令组合使用，如： <ul><li><code>caw</code>：删除当前单词并进入编辑模式</li><li><code>c&lt;数字&gt;j</code>：删除本行和下若干行并进入编辑模式</li></ul></li></ul></li></ul>',11),a=[o];function t(n,s){return l(),i("div",null,a)}const r=e(d,[["render",t],["__file","Vim.html.vue"]]),u=JSON.parse('{"path":"/blogs/Linux/Vim.html","title":"Vim","lang":"zh-CN","frontmatter":{"tags":["Linux基础"],"categories":["Linux"],"prev":{"text":"Ubuntu 24.04","link":"/blogs/Linux/Ubuntu24.04.html"},"next":{"text":"","link":"/blogs/Linux/wangzhanzhetengshilu.html"}},"headers":[{"level":2,"title":"模式","slug":"模式","link":"#模式","children":[]},{"level":2,"title":"普通模式","slug":"普通模式","link":"#普通模式","children":[{"level":3,"title":"移动光标","slug":"移动光标","link":"#移动光标","children":[]},{"level":3,"title":"删除、剪切、复制、粘贴","slug":"删除、剪切、复制、粘贴","link":"#删除、剪切、复制、粘贴","children":[]}]},{"level":2,"title":"插入模式","slug":"插入模式","link":"#插入模式","children":[]}],"git":{"createdTime":null,"updatedTime":null,"contributors":[]},"filePathRelative":"blogs/Linux/Vim.md"}');export{r as comp,u as data};
